import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Grid, 
  Card, 
  CardContent, 
  Typography, 
  Button, 
  CircularProgress,
  Paper,
  useTheme,
  alpha,
  Divider,
  IconButton,
  Menu,
  MenuItem,
  ListItemIcon,
  ListItemText,
  Tooltip,
  Avatar,
  Stack,
  LinearProgress,
  Chip
} from '@mui/material';
import { 
  LineChart, 
  Line, 
  BarChart,
  Bar,
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip as RechartsTooltip, 
  Legend, 
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  AreaChart,
  Area
} from 'recharts';
import { useNavigate } from 'react-router-dom';
import { 
  People as PeopleIcon, 
  Warning as WarningIcon, 
  TrendingUp as TrendingUpIcon, 
  AccessTime as AccessTimeIcon,
  LocalHospital as HospitalIcon,
  MoreVert as MoreVertIcon,
  Refresh as RefreshIcon,
  Timeline as TimelineIcon,
  Notifications as NotificationsIcon,
  ArrowForward as ArrowForwardIcon,
  Schedule as ScheduleIcon,
  PieChart as PieChartIcon,
  BarChart as BarChartIcon,
  ShowChart as ShowChartIcon,
  TimelineOutlined as TimelineOutlinedIcon,
  Assessment as AssessmentIcon,
  Error as ErrorIcon,
  CheckCircle as CheckCircleIcon,
  FiberManualRecord as FiberManualRecordIcon
} from '@mui/icons-material';
import { format, subDays, parseISO } from 'date-fns';

// Custom StatCard Component
const StatCard = ({ 
  title, 
  value, 
  icon, 
  color = 'primary', 
  trend, 
  subtitle,
  loading = false
}) => {
  const theme = useTheme();
  const trendColor = trend > 0 ? 'error' : 'success';
  const trendText = trend > 0 ? `${trend}% Increase` : `${Math.abs(trend)}% Decrease`;

  return (
    <Card 
      sx={{ 
        height: '100%',
        borderRadius: 2,
        boxShadow: '0 4px 20px 0 rgba(0,0,0,0.05)',
        transition: 'all 0.3s ease',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: '0 8px 25px 0 rgba(0,0,0,0.1)',
        },
      }}
    >
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="flex-start">
          <Box>
            <Typography 
              variant="subtitle2" 
              color="textSecondary" 
              sx={{ 
                fontWeight: 500,
                opacity: 0.8,
                mb: 0.5
              }}
            >
              {title}
            </Typography>
            {loading ? (
              <Box height={40} display="flex" alignItems="center">
                <CircularProgress size={24} />
              </Box>
            ) : (
              <>
                <Typography 
                  variant="h4" 
                  component="div" 
                  sx={{ 
                    fontWeight: 700,
                    background: `linear-gradient(45deg, ${theme.palette[color].main}, ${theme.palette[color].dark})`,
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    backgroundClip: 'text',
                    mb: 0.5
                  }}
                >
                  {value}
                </Typography>
                {subtitle && (
                  <Typography 
                    variant="body2" 
                    color="textSecondary"
                    sx={{ 
                      fontWeight: 500,
                      opacity: 0.8
                    }}
                  >
                    {subtitle}
                  </Typography>
                )}
              </>
            )}
          </Box>
          <Box
            sx={{
              backgroundColor: `${theme.palette[color].light}30`,
              borderRadius: '12px',
              width: 48,
              height: 48,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: theme.palette[color].main,
              flexShrink: 0,
            }}
          >
            {React.cloneElement(icon, { 
              style: { fontSize: 24 } 
            })}
          </Box>
        </Box>
        {trend !== undefined && !loading && (
          <Box 
            sx={{ 
              mt: 1.5,
              display: 'flex',
              alignItems: 'center',
              color: trend > 0 ? 'error.main' : 'success.main',
            }}
          >
            <TrendingUpIcon 
              sx={{ 
                transform: trend > 0 ? 'rotate(0deg)' : 'rotate(180deg)',
                mr: 0.5,
                fontSize: '1rem'
              }} 
            />
            <Typography 
              variant="caption" 
              sx={{ 
                fontWeight: 600,
                fontSize: '0.7rem',
                textTransform: 'uppercase',
                letterSpacing: '0.5px'
              }}
            >
              {trendText}
            </Typography>
          </Box>
        )}
      </CardContent>
    </Card>
  );
};

// Risk Badge Component
const RiskBadge = ({ risk, size = 'medium' }) => {
  const theme = useTheme();

  const getRiskStyles = () => {
    if (!risk) return { bgcolor: 'grey.300', color: 'grey.800', icon: null };

    const riskLower = risk.toLowerCase();
    if (riskLower.includes('high')) {
      return {
        bgcolor: 'error.light',
        color: 'error.contrastText',
        icon: <ErrorIcon fontSize={size === 'small' ? 'small' : 'medium'} />
      };
    } else if (riskLower.includes('medium')) {
      return {
        bgcolor: 'warning.light',
        color: 'warning.contrastText',
        icon: <WarningIcon fontSize={size === 'small' ? 'small' : 'medium'} />
      };
    } else if (riskLower.includes('low')) {
      return {
        bgcolor: 'success.light',
        color: 'success.contrastText',
        icon: <CheckCircleIcon fontSize={size === 'small' ? 'small' : 'medium'} />
      };
    }
    return {
      bgcolor: 'grey.300',
      color: 'grey.800',
      icon: null
    };
  };

  const styles = getRiskStyles();

  return (
    <Box 
      sx={{
        display: 'inline-flex',
        alignItems: 'center',
        px: size === 'small' ? 1 : 1.5,
        py: size === 'small' ? 0.25 : 0.5,
        borderRadius: 4,
        bgcolor: styles.bgcolor,
        color: styles.color,
        fontSize: size === 'small' ? '0.65rem' : '0.75rem',
        fontWeight: 600,
        textTransform: 'uppercase',
        letterSpacing: '0.5px',
        lineHeight: 1.2,
      }}
    >
      {styles.icon && (
        <Box mr={0.5} display="flex" alignItems="center">
          {styles.icon}
        </Box>
      )}
      {risk || 'N/A'}
    </Box>
  );
};

const API_URL = 'http://localhost:8000';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [dashboardData, setDashboardData] = useState({
    recentPredictions: [],
    riskDistribution: [],
    riskTrend: [],
    systemStatus: {}
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    setLoading(true);
    setError(null);

    try {
      // Fetch dashboard stats from API
      const response = await axios.get(`${API_URL}/dashboard/stats`);

      // Mock data for the dashboard
      const mockData = {
        stats: {
          totalPatients: 124,
          highRisk: 8,
          avgResponseTime: '2.5',
          trend: 12.5, // percentage
          totalPredictions: 356,
          accuracy: 94.2,
          avgRiskScore: 0.34
        },
        recentAlerts: [
          { 
            id: 1, 
            patientId: 'P1001', 
            name: 'Emma Wilson', 
            riskLevel: 'High', 
            time: '10:23 AM',
            age: '2 days',
            weight: '3.2 kg',
            avatar: '/static/images/avatar/1.jpg',
            vitalSigns: {
              heartRate: 185,
              oxygen: 88,
              bp: '85/45',
              temperature: 37.2
            }
          },
          { 
            id: 2, 
            patientId: 'P1002', 
            name: 'Noah Johnson', 
            riskLevel: 'Medium', 
            time: '09:45 AM',
            age: '1 day',
            weight: '2.9 kg',
            avatar: '/static/images/avatar/2.jpg',
            vitalSigns: {
              heartRate: 165,
              oxygen: 92,
              bp: '90/50',
              temperature: 36.8
            }
          },
          { 
            id: 3, 
            patientId: 'P1003', 
            name: 'Ava Williams', 
            riskLevel: 'High', 
            time: '09:12 AM',
            age: '3 days',
            weight: '3.1 kg',
            avatar: '/static/images/avatar/3.jpg',
            vitalSigns: {
              heartRate: 190,
              oxygen: 85,
              bp: '80/40',
              temperature: 37.5
            }
          },
        ],
        riskTrend: [
          { name: 'Jan', high: 5, medium: 12, low: 35 },
          { name: 'Feb', high: 7, medium: 15, low: 40 },
          { name: 'Mar', high: 6, medium: 14, low: 38 },
          { name: 'Apr', high: 8, medium: 16, low: 42 },
          { name: 'May', high: 10, medium: 18, low: 45 },
          { name: 'Jun', high: 12, medium: 20, low: 48 },
        ],
        riskDistribution: [
          { name: 'High Risk', value: 8, color: '#f44336' },
          { name: 'Medium Risk', value: 24, color: '#ff9800' },
          { name: 'Low Risk', value: 92, color: '#4caf50' },
        ],
        weeklyStats: {
          predictions: 42,
          alerts: 8,
          avgRiskScore: 0.34,
        },
        recentPredictions: [
          { id: 1, patientId: 'P1004', name: 'Liam Brown', riskScore: 0.82, status: 'High', timestamp: '2023-06-15T10:30:00' },
          { id: 2, patientId: 'P1005', name: 'Sophia Davis', riskScore: 0.65, status: 'Medium', timestamp: '2023-06-15T09:45:00' },
          { id: 3, patientId: 'P1006', name: 'Mia Miller', riskScore: 0.45, status: 'Medium', timestamp: '2023-06-14T16:20:00' },
        ]
      };

      // Format risk distribution for chart
      const riskDistributionData = [
        { name: 'Low', count: mockData.riskDistribution[2].value },
        { name: 'Moderate', count: mockData.riskDistribution[1].value },
        { name: 'High', count: mockData.riskDistribution[0].value },
        { name: 'Critical', count: 0 }
      ];

      // Format recent predictions
      const formattedPredictions = mockData.recentPredictions.map(pred => ({
        id: pred.patientId,
        timestamp: pred.timestamp,
        riskScore: pred.riskScore,
        alertTriggered: pred.status === 'High',
        risk_score: pred.riskScore,
        alert_triggered: pred.status === 'High'
      }));

      setDashboardData({
        recentPredictions: formattedPredictions,
        riskDistribution: riskDistributionData,
        riskTrend: response.data.risk_trend,
        systemStatus: response.data.system_status
      });

      setLoading(false);
    } catch (err) {
      setError('Failed to fetch dashboard data: ' + (err.response?.data?.detail || err.message));
      console.error('Error fetching dashboard data:', err);
      setLoading(false);
    }
  };

  const getRiskLevelColor = (score) => {
    if (score >= 0.8) return '#d32f2f'; // Critical - Red
    if (score >= 0.6) return '#f57c00'; // High - Orange
    if (score >= 0.4) return '#fbc02d'; // Moderate - Yellow
    return '#388e3c'; // Low - Green
  };

  const getRiskLevelText = (score) => {
    if (score >= 0.8) return 'CRITICAL';
    if (score >= 0.6) return 'HIGH';
    if (score >= 0.4) return 'MODERATE';
    return 'LOW';
  };

  const handleViewHistory = (patientId) => {
    navigate(`/history/${patientId}`);
  };

  const handleNewPrediction = () => {
    navigate('/predict');
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  if (loading && !dashboardData.recentPredictions.length) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ py: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
        <Typography variant="h4" component="h1">
          Cardiac Risk Dashboard
        </Typography>
        <Button 
          variant="contained" 
          color="primary"
          onClick={handleNewPrediction}
        >
          New Prediction
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 2 }}>
          <CircularProgress size={24} />
        </Box>
      )}

      <Grid container spacing={4}>
        {/* System Status Card */}
        <Grid item xs={12} md={4}>
          <Card elevation={3}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                System Status
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Typography variant="body2">Model Status:</Typography>
                  <Chip 
                    label={dashboardData.systemStatus.model_status || "Online"} 
                    color="success" 
                    size="small" 
                  />
                </Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Typography variant="body2">Alert System:</Typography>
                  <Chip 
                    label={dashboardData.systemStatus.alert_system || "Active"} 
                    color="success" 
                    size="small" 
                  />
                </Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Typography variant="body2">Last Update:</Typography>
                  <Typography variant="body2">
                    {dashboardData.systemStatus.last_update 
                      ? formatDate(dashboardData.systemStatus.last_update) 
                      : "Today, 10:30 AM"}
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Typography variant="body2">Total Predictions:</Typography>
                  <Typography variant="body2">
                    {dashboardData.systemStatus.total_predictions || 0}
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Typography variant="body2">Alerts Triggered:</Typography>
                  <Typography variant="body2">
                    {dashboardData.systemStatus.alerts_triggered || 0} 
                    ({dashboardData.systemStatus.alert_percentage 
                      ? dashboardData.systemStatus.alert_percentage.toFixed(1) + '%' 
                      : '0%'})
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* Risk Distribution Chart */}
        <Grid item xs={12} md={8}>
          <Paper elevation={3} sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Risk Distribution
            </Typography>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={dashboardData.riskDistribution}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Bar 
                  dataKey="count" 
                  name="Patients" 
                  fill="#1976d2"
                  barSize={40}
                />
              </BarChart>
            </ResponsiveContainer>
          </Paper>
        </Grid>

        {/* Risk Trend Chart */}
        <Grid item xs={12}>
          <Paper elevation={3} sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Average Risk Trend (Last 7 Days)
            </Typography>
            <ResponsiveContainer width="100%" height={250}>
              <LineChart data={dashboardData.riskTrend}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis domain={[0, 1]} />
                <Tooltip formatter={(value) => [`${(value * 100).toFixed(1)}%`, 'Average Risk']} />
                <Legend />
                <Line 
                  type="monotone" 
                  dataKey="avgRisk" 
                  name="Average Risk Score" 
                  stroke="#1976d2" 
                  activeDot={{ r: 8 }} 
                />
              </LineChart>
            </ResponsiveContainer>
          </Paper>
        </Grid>

        {/* Recent Predictions */}
        <Grid item xs={12}>
          <Paper elevation={3} sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Recent Predictions
            </Typography>
            {dashboardData.recentPredictions.length === 0 ? (
              <Typography variant="body1" sx={{ py: 2, textAlign: 'center' }}>
                No predictions available yet. Make a new prediction to see results here.
              </Typography>
            ) : (
              <List>
                {dashboardData.recentPredictions.map((prediction, index) => (
                  <React.Fragment key={`${prediction.id}-${index}`}>
                    {index > 0 && <Divider />}
                    <ListItem
                      secondaryAction={
                        <Button 
                          variant="outlined" 
                          size="small"
                          onClick={() => handleViewHistory(prediction.id)}
                        >
                          View History
                        </Button>
                      }
                    >
                      <ListItemText
                        primary={
                          <Box sx={{ display: 'flex', alignItems: 'center' }}>
                            <Typography variant="body1" sx={{ mr: 2 }}>
                              Patient {prediction.id}
                            </Typography>
                            <Chip 
                              label={getRiskLevelText(prediction.risk_score)} 
                              size="small"
                              sx={{ 
                                backgroundColor: getRiskLevelColor(prediction.risk_score),
                                color: 'white'
                              }}
                            />
                            {prediction.alert_triggered && (
                              <Chip 
                                label="Alert Sent" 
                                size="small"
                                color="error"
                                sx={{ ml: 1 }}
                              />
                            )}
                          </Box>
                        }
                        secondary={
                          <Typography variant="body2" color="text.secondary">
                            {formatDate(prediction.timestamp)} | Risk Score: {(prediction.risk_score * 100).toFixed(1)}%
                          </Typography>
                        }
                      />
                    </ListItem>
                  </React.Fragment>
                ))}
              </List>
            )}
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
